import React from 'react';
import { connect } from 'react-redux'
import axios from 'axios'
import { confirmAlert } from 'react-confirm-alert'; // Import
// import './article.css'
import 'react-confirm-alert/src/react-confirm-alert.css' // Import css

class Article extends React.Component {

    constructor(props) {
        super(props);
        this.state = { produit: [] };

    }
    componentDidMount() {
        axios.get('http://localhost:8080/projet/routes/atelier')
            .then(response => {
                console.log('i am a response', response)
                this.setState({ produit: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })        

    }

    toggle = () => {
        this.setState({
            modal: !this.state.modal,
        });
    }

    // componentDidMount() {
    //     var tab = []
    //     console.log('props: ', this.props)
    //     // axios.get("https://tsiorytahback.herokuapp.com/profil").then(res => {
    //         // axios.get("http://localhost:8080/atelier").then(res => {
    //             console.log('res comment: ', res.data)
    //         for (let i = 0; i < res.data.length; i++) {
    //             if (localStorage.getItem('id') === res.data[i].utilisateur) {
    //                 tab.push(res.data[i])
    //             }
    //         }
    //         this.setState({ comment: tab })
    //         console.log('comment: ', this.state.comment)
    //     })
    // }

    render() {
        return (
            // // AJOUT
            <center onSubmit={event => {
                event.preventDefault()
            }}>
                <table className="table table-bordered">
                    <tbody>
                        {
                            (this.state.produit.length > 0) ? (this.state.produit.map((obj) => {
                                return(<tr key={obj.id}>
                                    <td>
                                    {/*<p id="titre" onChange={this.handleChange}><img class="card-img-top img-thumbnail image" src={"https://tsiorytahback.herokuapp.com/profil/" + obj.photo} alt="fgbb" /></p>*/}
                                        <p id="titre" onChange={this.handleChange}><img class="card-img-top img-thumbnail image" src={"http://localhost:8080/projet/routes/atelier/" + obj.image} alt="vdsf" /></p>
                                        <p id="prix" style={{textAlign: "right"}} onChange={this.handleChange}>Heure: {obj.heure}</p>
                                        <p id="prix" style={{textAlign: "right"}} onChange={this.handleChange}>Date: {obj.date}</p>
                                        <p id="prix" style={{textAlign: "right"}} onChange={this.handleChange}>Places: {obj.places}</p> 
                                        <p id="prix" style={{textAlign: "right"}} onChange={this.handleChange}>Prix: {obj.prix} Â£</p>
                                    </td>
                                    <td>
                                    <strong>{obj.titre}</strong>
                                        <p id="description" onChange={this.handleChange}>{obj.description}</p>
                                        <p>
                                            {/* SUPPRESSION */}
                                            <button className="btn btn-danger"
                                                onClick={() => {
                                                    confirmAlert({
                                                        customUI: ({ onClose }) => {
                                                            return (
                                                                <center>
                                                                    <div className="custom-ui" id="popup">
                                                                        <table>
                                                                            <td>
                                                                                <img class="card-img-top img-thumbnail sary" src={"http://localhost:8080/projet/routes/atelier/" + obj.image} alt="za" /><br /> 
                                                                                // <img class="card-img-top img-thumbnail sary" src={"https://tsiorytahback.herokuapp.com/profil/" +obj.photo} alt="rer"/><br />
                                                                            </td>
                                                                            <td>
                                                                                <h6 className="text-pop">Suppression du Produit: </h6><br />
                                                                                <h6 className="text-pop">{obj.titre}</h6><br />
                                                                            </td>
                                                                        </table>
                                                                        <button className="btn btn-dark"
                                                                            onClick={() => {
                                                                                onClose();
                                                                            }}
                                                                        >
                                                                            OUI
                                                            </button><a href="#fsdf">&nbsp;&nbsp;</a>
                                                                        <button className="btn btn-dark" onClick={onClose}>NON</button>
                                                                    </div>
                                                                </center>
                                                            );
                                                        }
                                                    })
                                                }
                                                }
                                            >X</button>
                                            <a href="#fsdf">&nbsp;</a>
                                            {/* MODIFICATION */}
                                            <button className="btn btn-success"
                                                onClick={() => {
                                                    confirmAlert({
                                                        customUI: ({ onClose }) => {
                                                            return (
                                                                <form id='ID_FORMULAIRE'>
                                                                    <center>
                                                                        <div className="custom-ui" id="popup">
                                                                            <input name='inputStoreID' placeholder={obj.prix} id="entree" className="modif"></input><br />
                                                                            <p id="e"></p><br />

                                                                            <button className="btn btn-dark"
                                                                                onClick={(e) => {
                                                                                    obj.prix = document.forms['ID_FORMULAIRE'].elements['inputStoreID'].value //Affectation du contenu de l'input dans user.prix 
                                                                                    if (isNaN(obj.prix) || obj.prix === "") {
                                                                                        var valid = "Entrer un Nombre"
                                                                                        e.preventDefault()
                                                                                        document.getElementById('e').innerHTML = valid; //Affichage de la variable valid dans le paragraphe e
                                                                                    } else {
                                                                                        valid = "";
                                                                                        document.getElementById('e').innerHTML = valid;
                                                                                        onClose();
                                                                                    }
                                                                                }
                                                                                }
                                                                            >OK</button><a href="#fsdf">&nbsp;&nbsp;</a>

                                                                            <button className="btn btn-dark" onClick={onClose}>Annuler</button>
                                                                        </div>
                                                                    </center>
                                                                </form>
                                                            );
                                                        }
                                                    })
                                                }
                                                }
                                            >Edit</button>
                                        </p>
                                    </td>
                                </tr>)

                            })
                            ) : ('')

                        }
                    </tbody>
                </table>
            </center>


        )
    }
}

const mapStateToProps = (state) => {
    return {
        listeArticle: state
    }
}

export default connect(mapStateToProps)(Article)
