import React, { Component } from 'react';
import axios from 'axios';
import { Link } from 'react-router-dom'


export default class ListTout extends Component {

    constructor(props) {
        super(props);
        this.state = { produit: [] };

    }
    componentDidMount() {
        axios.get('http://localhost:8080/api/users/newArticle')
            .then(response => {
                console.log('i am a response', response)
                this.setState({ produit: response.data });
                console.log('i am a produit', this.state.produit)
                localStorage.setItem('loc', response.data[1].place)
            })
            .catch(function (error) {
                console.log(error);
            })



    }

    liste() {
        return <div id="event">
    <div class="bg-color" class="section-padding">
    {
                    (this.state.produit.length > 0) ? (this.state.produit.map((obj) => {
      return<div class="container">
        <div class="row" key={obj._id}>
          <div class="col-xs-12 text-center" style="padding:60px;">
            <h1 class="header-h">Up Coming events</h1>
          </div>
          <div class="col-md-12" style="padding-bottom:60px;">
            <div class="item active left">
              <div class="col-md-6 col-sm-6 left-images">
                <img className="img-fluid " id="mgList"
                    src={'http://localhost:8080/api/users/newArticleImage/' + obj.image} alt="pdp" />
              </div>
              <div class="col-md-6 col-sm-6 details-text">
                <div class="content-holder">
                  <h2>{obj.titre}</h2>
                  <p>{obj.descrition}</p>
                  <address>
                              <strong>Place: </strong>
                              {obj.place}
                              <br/>
                              <strong>Time: </strong>
                             {obj.duree}
                            </address>
                  <a class="btn btn-imfo btn-read-more" href="events-details.html">Read more</a>
                </div>
              </div>
            </div>
          </div>
        </div>
      </div>
       })) : ('')
    }
    </div>
  </div>
    }
    render() {
        return (
            <div>
                {this.liste()}
            </div>
        );
    }
}
