import React, { Component } from 'react';
import axios from 'axios';
import './listeAtelier.css';


export default class ListTout extends Component {

    constructor(props) {
        super(props);
        this.state = { produit: [] };

    }
    componentDidMount() {
        axios.get('http://localhost:8080/projet/routes/atelier')
            .then(response => {
                console.log('i am a response', response)
                this.setState({ produit: response.data });
            })
            .catch(function (error) {
                console.log(error);
            })



    }

    liste() {
        return <div>
            <div className="table-responsive">

                {
                    (this.state.produit.length > 0) ? (this.state.produit.map((obj) => {
                        return <div >
                            <div id="ligne" className="row" key={obj._id}>
                                <div className="col-md-1"></div>
                                <div className="col-md-6">
                                    <img width="400px" height="450" src={'http://localhost:8080/projet/routes/atelier/' + obj.image} alt="pdp" />
                                </div>
                                <div className="col-md-2">
                                    <div className="row">
                                        <div className="container-fluid">
                                            <h2>
                                                {obj.titre}
                                            </h2>
                                            <p><strong>{obj.prix}</strong></p>
                                        </div>
                                        <div className="container-fluid">
                                            <div className="row">
                                                <div className="col-xs-6">
                                                    <button><span className=" glyphicon glyphicon-shopping-cart "></span>S'inscrire</button>
                                                </div>
                                                <div className="col-xs-6">
                                                    <button onClick={() => {
                                                        return (
                                                            <div className="row">
                                                                <div className="col-md-6">
                                                                    <img width="400px" height="450" src={'http://localhost:8080/user/produitImage/' + obj.image} alt="pdp" />
                                                                </div>
                                                            </div>
                                                        )
                                                    }}><span className=" glyphicon glyphicon-eye-open " ></span>Voir detail</button>
                                                </div>
                                            </div>
                                        </div>

                                    </div>
                                </div>
                                <div className="col-md-3"></div>
                            </div>
                        </div>

                    })) : ('')
                }

                <div class="card">
                  <a href="https://damienflandrin.fr/blog/post/tutoriel-realiser-une-carte-en-css">
                  <div class="card-image">
                    <img src="https://damienflandrin.fr/storage/tutoriel-realiser-une-carte-en-css/card-image.jpg" alt="Orange" />
                  </div>

                  <div class="card-body">

                    <div class="card-date">
                      <time>20 Novembre 1992</time>
                    </div>

                    <div class="card-title">
                      <h3>Lorem Ipsum</h3>
                    </div>

                    <div class="card-excerpt">
                      <p> Lorem ipsum dolor sit amet, consectetur adipisicing elit. Aliquam voluptatibus autem consectetur voluptate facere at, omnis ab optio placeat officiis! Animi modi harum enim quia veniam consectetur unde autem inventore.</p>
                    </div>

                  </div>
                  </a>
                </div>

                <a  href="https://damienflandrin.fr/blog/post/tutoriel-realiser-une-carte-en-css"  target="blank">Voir le tutoriel sur https://www.damienflandrin.fr</a>


            </div>
        </div>
    }
    render() {
        return (
            <div>
                {this.liste()}
            </div>
        );
    }
}
